var openSansFont = new FontFaceObserver("Open Sans"), ubuntuFont = new FontFaceObserver("Ubuntu"), fontAwesomeFont = new FontFaceObserver("FontAwesome");

openSansFont.load().then(function() {
    $("body").addClass("opensans_font");
}), ubuntuFont.load().then(function() {
    $(":header").addClass("ubuntu_font"), $(".navbar-custom").addClass("ubuntu_font"), 
    $(".intro-text .name").addClass("ubuntu_font");
}), fontAwesomeFont.load().then(function() {
    $("body").addClass("awesome_font");
});